// src/pages/JobsPage.jsx
import React, { useState, useEffect } from "react";
import JobCard from "../components/JobCard";

const JobsPage = () => {
  const [jobs, setJobs] = useState([
    {
      id: 1,
      title: "Frontend Developer",
      company: "Tech Corp",
      location: "New York, USA",
      description: "We are looking for a talented frontend developer...",
      source: "LinkedIn",
    },
    {
      id: 2,
      title: "Backend Developer",
      company: "InnovateX",
      location: "San Francisco, USA",
      description: "Join our team as a backend developer...",
      source: "Indeed",
    },
    {
      id: 3,
      title: "UI/UX Designer",
      company: "Designify",
      location: "Los Angeles, USA",
      description: "Looking for a UI/UX Designer with experience in Figma...",
      source: "Glassdoor",
    },
  ]);

  useEffect(() => {
    // Fetch job listings from API if needed
    // Example:
    // fetch('/api/jobs')
    //   .then(res => res.json())
    //   .then(data => setJobs(data))
  }, []);

  return (
    <div className="p-6 space-y-6">
      <h1 className="text-3xl font-bold text-[#00ADB5]">Job Listings</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {jobs.map((job) => (
          <JobCard key={job.id} job={job} />
        ))}
      </div>
    </div>
  );
};

export default JobsPage;
