import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user data from localStorage
    const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));

    if (loggedInUser) {
      setUserData(loggedInUser); // Use actual logged-in user data
    } else {
      // Redirect to login if no user is logged in
      navigate("/login");
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser"); // Clear user data from localStorage
    navigate("/login"); // Redirect to login page
  };

  // Show loading if userData is not yet set
  if (!userData) {
    return (
      <div className="min-h-screen bg-gray-100 pt-16 flex justify-center items-center">
        <div>Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 pt-16 flex justify-center items-center">
      <div className="container mx-auto p-8 bg-white shadow-xl rounded-lg max-w-lg w-full">
        <h1 className="text-4xl font-semibold text-center mb-8 text-gray-800">Your Profile</h1>
        <div className="space-y-6">
          <div className="flex justify-between">
            <strong className="text-lg text-gray-600">Full Name:</strong>
            <span className="text-lg text-gray-900">{userData.fullName}</span>
          </div>
          <div className="flex justify-between">
            <strong className="text-lg text-gray-600">Email:</strong>
            <span className="text-lg text-gray-900">{userData.email}</span>
          </div>
          <div className="flex justify-between">
            <strong className="text-lg text-gray-600">Phone:</strong>
            <span className="text-lg text-gray-900">{userData.phone}</span>
          </div>
          <div className="flex justify-between">
            <strong className="text-lg text-gray-600">Location:</strong>
            <span className="text-lg text-gray-900">{userData.location}</span>
          </div>
          <div className="flex justify-between">
            <strong className="text-lg text-gray-600">Gender:</strong>
            <span className="text-lg text-gray-900">{userData.gender}</span>
          </div>
          <div className="flex justify-between">
            <strong className="text-lg text-gray-600">Skills:</strong>
            <span className="text-lg text-gray-900">{userData.skills.join(", ")}</span>
          </div>
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-4 py-2 rounded-full hover:bg-red-600 mt-6 w-full max-w-xs mx-auto"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
