// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-blue-600 shadow-lg">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <Link to="/" className="text-white text-2xl font-bold">Job Portal</Link>
        <button onClick={() => setIsOpen(!isOpen)} className="md:hidden text-white">
          {/* Hamburger icon */}
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={isOpen ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16M4 18h16"} />
          </svg>
        </button>
        <div className={`md:flex ${isOpen ? "block" : "hidden"}`}>
          <Link to="/" className="text-white px-4 py-2">Home</Link>
          <Link to="/jobs" className="text-white px-4 py-2">Jobs</Link>
          <Link to="/signup" className="text-white px-4 py-2">Sign Up</Link>
          <Link to="/login" className="text-white px-4 py-2">Login</Link>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
