// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import Profile from "./pages/Profile";
import JobsPage from "./pages/JobsPage";
import PrivateRoute from "./components/PrivateRoute";  // Import PrivateRoute

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Check if user is already authenticated on page load from localStorage
    const loggedInUser = localStorage.getItem("loggedInUser");
    setIsAuthenticated(!!loggedInUser); // If loggedInUser exists, set authenticated to true
  }, []);

  // Handle login success by updating the isAuthenticated state and saving it to localStorage
  const handleLogin = (user) => {
    localStorage.setItem("loggedInUser", JSON.stringify(user)); // Save user data to localStorage
    setIsAuthenticated(true);
  };

  // Handle logout by clearing user data from localStorage and updating the state
  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <Navbar isAuthenticated={isAuthenticated} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route
          path="/login"
          element={<Login handleLogin={handleLogin} />}
        />

        {/* Protect Jobs Route: Only accessible if authenticated */}
        <PrivateRoute
          path="/jobs"
          element={<JobsPage />}
          isAuthenticated={isAuthenticated}
        />

        {/* Protect Profile Route: Only show if authenticated */}
        <Route
          path="/profile"
          element={isAuthenticated ? <Profile /> : <Login handleLogin={handleLogin} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
