const Profile = ({ updateUserBackend, savedJobs = [] }) => {
  const [userData, setUserData] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
    if (loggedInUser) {
      setUserData(loggedInUser);
    } else {
      navigate("/login");
    }
  }, [navigate]);

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleFieldChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSave = () => {
    localStorage.setItem("loggedInUser", JSON.stringify(userData));
    updateUserBackend(userData);
    setIsEditing(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    navigate("/login");
  };

  if (!userData) {
    return <div className="min-h-screen flex justify-center items-center">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100 py-16 px-6">
      <div className="container mx-auto max-w-4xl">
        <div className="bg-white shadow-lg rounded-lg p-8 mb-8">
          <h1 className="text-3xl font-semibold mb-6">Your Profile</h1>
          <div className="space-y-4">
            {Object.keys(userData).map((field) =>
              field !== "savedJobs" ? (
                <div key={field} className="flex justify-between items-center">
                  <strong className="text-gray-600 capitalize">{field.replace(/([A-Z])/g, " $1")}:</strong>
                  {isEditing ? (
                    <input
                      type="text"
                      name={field}
                      value={userData[field]}
                      onChange={handleFieldChange}
                      className="border px-3 py-2 rounded-lg w-2/3"
                    />
                  ) : (
                    <span className="text-gray-900">{userData[field]}</span>
                  )}
                </div>
              ) : null
            )}
          </div>
          <div className="mt-6">
            {isEditing ? (
              <button
                onClick={handleSave}
                className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition duration-300"
              >
                Save Changes
              </button>
            ) : (
              <button
                onClick={handleEditToggle}
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition duration-300"
              >
                Edit Profile
              </button>
            )}
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition duration-300 ml-4"
            >
              Logout
            </button>
          </div>
        </div>

        {/* Saved Jobs Section */}
        <div className="bg-white shadow-lg rounded-lg p-8">
          <h2 className="text-2xl font-semibold mb-4">Saved Jobs</h2>
          {savedJobs && savedJobs.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {savedJobs.map((job, index) => (
                <div
                  key={index}
                  className="border p-4 rounded-lg shadow flex flex-col justify-between bg-gray-50"
                >
                  <h3 className="text-lg font-semibold">{job.title}</h3>
                  <p className="text-sm text-gray-600">{job.company}</p>
                  <p className="text-sm text-gray-600">{job.location}</p>
                  <button
                    className="bg-[#00ADB5] text-white px-3 py-2 rounded-lg mt-4 hover:bg-[#00A8A0] transition duration-300"
                    onClick={() => window.open(job.applyLink, "_blank")}
                  >
                    Apply Now
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">You have no saved jobs.</p>
          )}
        </div>
      </div>
    </div>
  );
};
