// src/components/Navbar.jsx
import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="bg-[#393E46] text-[#EEEEEE] fixed w-full top-0 left-0 z-50 shadow-md">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo */}
        <div className="text-xl font-bold text-[#00ADB5]">
          <Link to="/">Job Portal</Link>
        </div>

        {/* Hamburger Icon for small screens */}
        <div className="sm:hidden flex items-center">
          <button onClick={toggleMenu} className="text-2xl">
            <i className="fas fa-bars"></i>
          </button>
        </div>

        {/* Navbar Links */}
        <div
          className={`sm:flex flex-col sm:flex-row items-center space-x-6 sm:space-x-8 ${isMenuOpen ? "block" : "hidden"}`}
        >
          <Link to="/" className="hover:text-[#00ADB5]">
            Home
          </Link>
          <Link to="/jobs" className="hover:text-[#00ADB5]">
            Jobs
          </Link>
          <Link to="/signup" className="hover:text-[#00ADB5]">
            Signup
          </Link>
          <Link to="/login" className="hover:text-[#00ADB5]">
            Login
          </Link>
          <Link to="/contact" className="hover:text-[#00ADB5]">
            Contact Us
          </Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
