// src/components/Navbar.js
import React from "react";
import { Link } from "react-router-dom";

const Navbar = ({ isAuthenticated }) => {
  return (
    <nav className="bg-gray-800 text-white p-4 fixed w-full top-0 left-0 z-50 shadow-lg">
      <div className="flex justify-between items-center">
        {/* Logo Section */}
        <div className="text-2xl font-bold">
          <Link to="/" className="hover:bg-gray-700 p-2 rounded">Job</Link>
        </div>

        {/* Links Section */}
        <div className="space-x-6 flex items-center ml-4"> {/* Added ml-4 for space on the left */}
          <Link to="/jobs" className="hover:bg-gray-700 p-2 rounded">Jobs</Link>
          {!isAuthenticated && (
            <>
              <Link to="/signup" className="hover:bg-gray-700 p-2 rounded">Sign Up</Link>
              <Link to="/login" className="hover:bg-gray-700 p-2 rounded">Login</Link>
            </>
          )}
          {/* Profile Link (shown only when logged in) */}
          {isAuthenticated && (
            <Link to="/profile" className="hover:bg-gray-700 p-2 rounded">Profile</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
