import React, { useState, useEffect } from "react";
import JobCard from "../components/JobCard";

const JobsPage = () => {
  const [activeTab, setActiveTab] = useState("forYou");
  const [recommendedJobs, setRecommendedJobs] = useState([]);
  const [allJobs, setAllJobs] = useState([]);

  useEffect(() => {
    // Mock Recommended Jobs
    const mockRecommendedJobs = [
      {
        id: 1,
        title: "AI Specialist",
        company: "DeepMind",
        location: "London, UK",
        description: "Exciting role in AI research...",
        source: "Glassdoor Recommendations",
      },
    ];
    setRecommendedJobs(mockRecommendedJobs);

    // Mock All Jobs
    const mockAllJobs = [
      {
        id: 2,
        title: "Frontend Developer",
        company: "Tech Corp",
        location: "New York, USA",
        description: "We are looking for a talented frontend developer...",
        source: "Glassdoor",
      },
      {
        id: 3,
        title: "Backend Developer",
        company: "InnovateX",
        location: "San Francisco, USA",
        description: "Join our team as a backend developer...",
        source: "Glassdoor",
      },
    ];
    setAllJobs(mockAllJobs);
  }, []);

  return (
    <div className="p-6">
      {/* Search Bar */}
      <div className="flex justify-center items-center gap-2 mb-6">
        <input
          type="text"
          placeholder="Find your perfect job"
          className="p-3 border rounded-l-lg w-3/4"
        />
        <input
          type="text"
          placeholder="Location"
          className="p-3 border rounded-r-lg w-1/4"
        />
      </div>

      {/* Tabs */}
      <div className="flex justify-around border-b mb-6">
        <button
          className={`pb-2 text-lg font-semibold ${
            activeTab === "forYou" ? "border-b-2 border-green-500" : ""
          }`}
          onClick={() => setActiveTab("forYou")}
        >
          For you
        </button>
        <button
          className={`pb-2 text-lg font-semibold ${
            activeTab === "search" ? "border-b-2 border-green-500" : ""
          }`}
          onClick={() => setActiveTab("search")}
        >
          Search
        </button>
      </div>

      {/* Tab Content */}
      {activeTab === "forYou" && (
        <div>
          <h2 className="text-2xl font-semibold mb-4">Recommended Jobs</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {recommendedJobs.map((job) => (
              <JobCard key={job.id} job={job} />
            ))}
          </div>
        </div>
      )}

      {activeTab === "search" && (
        <div>
          <h2 className="text-2xl font-semibold mb-4">All Jobs</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {allJobs.map((job) => (
              <JobCard key={job.id} job={job} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default JobsPage;
