import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation

const skillsOptions = [
  "JavaScript",
  "Python",
  "Java",
  "C++",
  "React",
  "Node.js",
  "SQL",
  "CSS",
  "HTML",
  "AWS",
];

const Signup = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
    gender: "",
    location: "",
    skills: [],
  });

  const [dropdownOpen, setDropdownOpen] = useState(false); // Dropdown state
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSkillSelect = (skill) => {
    if (!formData.skills.includes(skill)) {
      setFormData({ ...formData, skills: [...formData.skills, skill] });
    }
  };

  const handleSkillRemove = (skill) => {
    setFormData({
      ...formData,
      skills: formData.skills.filter((s) => s !== skill),
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission (e.g., send data to API)
    console.log("Form Submitted", formData);
    navigate("/login"); // Redirect to login page after successful signup
  };

  return (
    <div className="min-h-screen bg-[#222831] flex items-center justify-center pt-16">
      {/* Container for the form */}
      <div className="container mx-auto p-8 bg-[#393E46] text-white shadow-xl rounded-lg max-w-3xl">
        <h1 className="text-3xl font-extrabold text-center mb-8 text-[#00ADB5]">
          Create Your Account
        </h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Form Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-semibold">
                Full Name <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold">
                Email Address <span className="text-red-500">*</span>
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold">
                Phone Number <span className="text-red-500">*</span>
              </label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold">
                Location/City <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold">
                Password <span className="text-red-500">*</span>
              </label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold">
                Confirm Password <span className="text-red-500">*</span>
              </label>
              <input
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold">
                Gender <span className="text-red-500">*</span>
              </label>
              <select
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              >
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-semibold">
                Skills <span className="text-red-500">*</span>
              </label>
              <div className="relative">
                <div
                  className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none"
                  onClick={() => setDropdownOpen(!dropdownOpen)}
                >
                  {formData.skills.length > 0
                    ? formData.skills.join(", ")
                    : "Select Skills"}
                </div>
                {dropdownOpen && (
                  <div className="absolute z-10 bg-[#222831] border border-[#00ADB5] rounded-md mt-2 max-h-40 overflow-y-auto w-full">
                    {skillsOptions.map((skill) => (
                      <div
                        key={skill}
                        onClick={() => handleSkillSelect(skill)}
                        className="p-2 hover:bg-[#393E46] cursor-pointer"
                      >
                        {skill}
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <div className="flex flex-wrap gap-2 mt-2">
                {formData.skills.map((skill) => (
                  <div
                    key={skill}
                    className="flex items-center bg-[#00ADB5] text-[#222831] px-3 py-1 rounded-md"
                  >
                    {skill}
                    <button
                      type="button"
                      onClick={() => handleSkillRemove(skill)}
                      className="ml-2 text-sm text-white hover:text-red-500"
                    >
                      &times;
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
          {/* Submit Button */}
          <div className="text-center">
            <button
              type="submit"
              className="bg-[#00ADB5] text-white py-3 px-6 rounded-md hover:bg-[#00A8A0] transition duration-300"
            >
              Sign Up
            </button>
          </div>
          {/* Redirect to Login */}
          <div className="text-center mt-4">
            <p className="text-sm">
              Already have an account?{" "}
              <span
                className="text-[#00ADB5] cursor-pointer hover:underline"
                onClick={() => navigate("/login")}
              >
                Login
              </span>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Signup;
