// src/pages/JobsPage.jsx
import React, { useState, useEffect } from "react";
import JobCard from "../components/JobCard";  // Correct the import path here

const JobsPage = () => {
  const [jobs, setJobs] = useState([
    {
      id: 1,
      title: "Software Engineer",
      company: "TechCorp",
      location: "New York, NY",
      jobType: "Full-time",
      shortDescription: "Looking for a passionate Software Engineer.",
      source: "Indeed",
      sourceUrl: "https://www.indeed.com",
    },
    {
      id: 2,
      title: "Product Manager",
      company: "InnovateX",
      location: "San Francisco, CA",
      jobType: "Full-time",
      shortDescription: "We need a Product Manager to lead our projects.",
      source: "LinkedIn",
      sourceUrl: "https://www.linkedin.com",
    },
  ]);

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Job Listings</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {jobs.map((job) => (
          <JobCard key={job.id} job={job} />
        ))}
      </div>
    </div>
  );
};

export default JobsPage;
