// src/components/JobCard.jsx
// src/components/FancyJobCard.jsx
import React from 'react';
import { FaBuilding, FaMapMarkerAlt, FaExternalLinkAlt } from 'react-icons/fa';

const FancyJobCard = ({ job }) => {
  return (
    <div className="bg-white rounded-lg shadow-lg p-6 transform transition duration-300 hover:scale-105 hover:shadow-xl">
      <h2 className="text-2xl font-semibold text-gray-800 mb-2">{job.title}</h2>
      
      <div className="flex items-center text-gray-600 mb-4">
        <FaBuilding className="mr-2 text-gray-400" />
        <span>{job.company}</span>
      </div>
      
      <div className="flex items-center text-gray-600 mb-4">
        <FaMapMarkerAlt className="mr-2 text-gray-400" />
        <span>{job.location}</span>
      </div>
      
      <p className="text-gray-700 mb-4">{job.shortDescription}</p>
      
      <a 
        href={job.sourceUrl} 
        target="_blank" 
        rel="noopener noreferrer" 
        className="flex items-center text-blue-500 hover:text-blue-700 font-semibold mt-4"
      >
        Apply here <FaExternalLinkAlt className="ml-2" />
      </a>
    </div>
  );
};

export default FancyJobCard;
