// src/pages/LandingPage.jsx
import React, { useState } from 'react';
import Navbar from '../components/Navbar';

const LandingPage = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    // Handle the search logic here (e.g., filter jobs based on searchQuery)
    console.log('Searching for:', searchQuery);
  };

  return (
    <div className="bg-[#222831] min-h-screen text-[#EEEEEE] flex flex-col justify-center items-center py-10">
      {/* Navbar */}
      <Navbar />

      {/* Header Section */}
      <div className="text-center space-y-4 mb-8">
        <h1 className="text-4xl font-bold text-[#00ADB5]">
          Find Your Dream Job
        </h1>
        <p className="text-lg max-w-3xl mx-auto">
          Search for jobs by job title, company, or location. Type them all in the same field!
        </p>
      </div>

      {/* Search Form */}
      <form onSubmit={handleSearch} className="w-full max-w-3xl mx-auto flex flex-col sm:flex-row justify-center items-center space-y-4 sm:space-y-0 sm:space-x-4">
        {/* Single Search Input Field */}
        <input 
          type="text" 
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search by Job Title, Company, or Location"
          className="w-full sm:w-2/3 px-4 py-2 rounded-md text-gray-900"
        />

        {/* Search Button */}
        <button 
          type="submit"
          className="w-full sm:w-auto bg-[#00ADB5] text-[#222831] px-6 py-2 rounded mt-4 sm:mt-0 hover:bg-[#00A7A1]"
        >
          Search Jobs
        </button>
      </form>
    </div>
  );
};

export default LandingPage;
