// src/pages/Login.jsx
import React, { useState } from "react";

const Login = ({ setIsAuthenticated }) => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulate login process, replace with actual authentication logic
    if (formData.email && formData.password) {
      setIsAuthenticated(true); // After successful login, set user as authenticated
    }
  };

  return (
    <div className="min-h-screen bg-[#222831] pt-20">
      <div className="container mx-auto p-6 bg-[#393E46] text-white shadow-xl rounded-lg max-w-lg">
        <h1 className="text-3xl font-extrabold text-center mb-6 text-[#00ADB5]">Login</h1>
        <form onSubmit={handleSubmit}>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-semibold">Email Address</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-semibold">Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                required
              />
            </div>
          </div>

          <div className="mt-6 text-center">
            <button
              type="submit"
              className="bg-[#00ADB5] text-white py-2 px-6 rounded-md hover:bg-[#00A8A0] transition duration-300"
            >
              Login
            </button>
          </div>
        </form>

        <div className="mt-4 text-center text-sm">
          <p className="text-gray-400">
            Don't have an account?{" "}
            <a href="/signup" className="text-[#00ADB5] hover:text-[#00A8A0]">
              Sign Up
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
