// src/pages/JobsPage.jsx
import React, { useState, useEffect } from 'react';
import JobCard from './components/JobCard'; // Correct import path

const JobsPage = () => {
  const [jobs, setJobs] = useState([]);

  const exampleJobs = [
    { id: 1, title: "Software Engineer", company: "TechCorp", location: "New York, NY", shortDescription: "Looking for a passionate Software Engineer.", sourceUrl: "https://www.indeed.com" },
    { id: 2, title: "Product Manager", company: "InnovateX", location: "San Francisco, CA", shortDescription: "We need a Product Manager to lead our projects.", sourceUrl: "https://www.linkedin.com" },
    { id: 3, title: "UX/UI Designer", company: "CreativeMind", location: "Los Angeles, CA", shortDescription: "Seeking a skilled UX/UI Designer.", sourceUrl: "https://www.glassdoor.com" },
    { id: 4, title: "Data Scientist", company: "DataVision", location: "Chicago, IL", shortDescription: "Join our team of data scientists to work on innovative projects.", sourceUrl: "https://www.indeed.com" },
  ];

  useEffect(() => {
    setJobs(exampleJobs);  // You can replace this with actual API call later
  }, []);

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Job Listings</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {jobs.length === 0 ? (
          <p>Loading job listings...</p>
        ) : (
          jobs.map((job) => (
            <JobCard key={job.id} job={job} />
          ))
        )}
      </div>
    </div>
  );
};

export default JobsPage;
