import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

 const handleLogin = (e) => {
  e.preventDefault();
  const storedUsers = JSON.parse(localStorage.getItem("users")) || [];
  
  // Find the user with matching email and password
  const user = storedUsers.find(
    (user) =>
      user.email === formData.email && user.password === formData.password
  );
  
  if (user) {
    alert("Login successful!");
    localStorage.setItem("loggedInUser", JSON.stringify(user));
    navigate("/jobs"); // Redirect to jobs page
  } else {
    alert("Invalid email or password. Please try again.");
  }
};


  return (
    <div className="min-h-screen bg-[#222831] flex items-center justify-center pt-16">
      <div className="container mx-auto p-6 bg-[#393E46] text-white shadow-xl rounded-lg max-w-sm">
        <h1 className="text-3xl font-extrabold text-center mb-8 text-[#00ADB5]">
          Login
        </h1>
        <form onSubmit={handleLogin} className="space-y-6">
          {/* Email */}
          <div>
            <label className="block text-sm font-semibold">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
              required
            />
          </div>

          {/* Password */}
          <div>
            <label className="block text-sm font-semibold">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
              required
            />
          </div>

          {/* Submit Button */}
          <div className="text-center">
            <button
              type="submit"
              className="bg-[#00ADB5] text-white py-3 px-6 rounded-md hover:bg-[#00A8A0] transition duration-300"
            >
              Login
            </button>
          </div>
        </form>

        {/* Signup Redirect */}
        <div className="text-center mt-4">
          Don't have an account?{" "}
          <button
            onClick={() => navigate("/signup")}
            className="text-[#00ADB5] hover:underline"
          >
            Sign up here
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
