import React, { useEffect } from "react";
import { Link } from "react-router-dom";

const Navbar = ({ isAuthenticated, setIsAuthenticated }) => {
  useEffect(() => {
    // Update the isAuthenticated state based on the localStorage whenever the component mounts
    const loggedInUser = localStorage.getItem("loggedInUser");
    setIsAuthenticated(!!loggedInUser); // Update isAuthenticated based on loggedInUser
  }, [setIsAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser"); // Remove the user data from localStorage
    setIsAuthenticated(false); // Update state to reflect logged-out status
  };

  return (
    <nav className="bg-gray-800 text-white p-4 fixed w-full top-0 left-0 z-50 shadow-lg">
      <div className="flex justify-between items-center">
        {/* Logo Section */}
        <div className="text-2xl font-bold">
          <Link to="/" className="hover:bg-gray-700 p-2 rounded">Job-Currator</Link>
        </div>

        {/* Links Section */}
        <div className="space-x-6 flex items-center ml-4">
          <Link to="/jobs" className="hover:bg-gray-700 p-2 rounded">Jobs</Link>

          {/* Show Sign Up and Login only if not authenticated */}
          {!isAuthenticated && (
            <>
              <Link to="/signup" className="hover:bg-gray-700 p-2 rounded">Sign Up</Link>
              <Link to="/login" className="hover:bg-gray-700 p-2 rounded">Login</Link>
            </>
          )}

          {/* Show Profile link if authenticated */}
          {isAuthenticated && (
            <>
              <Link to="/profile" className="hover:bg-gray-700 p-2 rounded">Profile</Link>
              <button
                onClick={handleLogout}
                className="bg-red-500 text-white p-2 rounded hover:bg-red-700"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
