// src/components/Navbar.js
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if a user is logged in
    const loggedInUser = localStorage.getItem("loggedInUser");
    setIsAuthenticated(!!loggedInUser); // Set true if loggedInUser exists
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser"); // Clear user data
    setIsAuthenticated(false); // Update state
    navigate("/login"); // Redirect to login page
  };

  return (
    <nav className="bg-gray-800 text-white p-4 fixed w-full top-0 left-0 z-50 shadow-lg">
      <div className="flex justify-between items-center">
        {/* Logo Section */}
        <div className="text-2xl font-bold">
          <Link to="/" className="hover:bg-gray-700 p-2 rounded">
            Job-Curator
          </Link>
        </div>

        {/* Links Section */}
        <div className="space-x-6 flex items-center ml-4">
          <Link to="/jobs" className="hover:bg-gray-700 p-2 rounded">
            Jobs
          </Link>
          {!isAuthenticated ? (
            <>
              <Link to="/signup" className="hover:bg-gray-700 p-2 rounded">
                Sign Up
              </Link>
              <Link to="/login" className="hover:bg-gray-700 p-2 rounded">
                Login
              </Link>
            </>
          ) : (
            <>
              <Link to="/profile" className="hover:bg-gray-700 p-2 rounded">
                Profile
              </Link>
              <button
                onClick={handleLogout}
                className="hover:bg-gray-700 p-2 rounded"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
