import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import JobCard from "../components/JobCard";

const JobsPage = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("forYou");
  const [recommendedJobs, setRecommendedJobs] = useState([]);
  const [allJobs, setAllJobs] = useState([]);
  const [searchTitle, setSearchTitle] = useState("");
  const [searchCompany, setSearchCompany] = useState(""); // New state for company filter
  const [searchLocation, setSearchLocation] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Authentication state

  useEffect(() => {
    // Check if user is logged in
    const user = localStorage.getItem("loggedInUser");
    if (user) {
      setIsLoggedIn(true); // Set logged in state if user is found
    } else {
      setIsLoggedIn(false); // Set logged out state if no user found
      navigate("/login"); // Redirect to login page if not logged in
    }

    // Mock Recommended Jobs
    const mockRecommendedJobs = [
      {
        id: 2,
        title: "Full-Stack Developer",
        company: "Google",
        location: "Mountain View, CA, USA",
        description: "Work on cutting-edge web technologies...",
        source: "LinkedIn",
      },
      {
        id: 3,
        title: "Data Scientist",
        company: "Meta",
        location: "Menlo Park, CA, USA",
        description: "Analyze and interpret complex data...",
        source: "Indeed",
      },
      {
        id: 4,
        title: "Machine Learning Engineer",
        company: "Amazon",
        location: "Seattle, WA, USA",
        description: "Develop and deploy ML models...",
        source: "Company Careers",
      },
      {
        id: 5,
        title: "Cloud Solutions Architect",
        company: "Microsoft",
        location: "Redmond, WA, USA",
        description: "Design scalable cloud architectures...",
        source: "Employee Referral",
      },
    ];
    setRecommendedJobs(mockRecommendedJobs);

    // Mock All Jobs
    const mockAllJobs = [
      {
        id: 2,
        title: "Frontend Developer",
        company: "Tech Corp",
        location: "New York, USA",
        description: "We are looking for a talented frontend developer...",
        source: "Glassdoor",
      },
      {
        id: 3,
        title: "Backend Developer",
        company: "InnovateX",
        location: "San Francisco, USA",
        description: "Join our team as a backend developer...",
        source: "Glassdoor",
      },
      {
        id: 11,
        title: "Software Engineer",
        company: "Tesla",
        location: "Palo Alto, CA, USA",
        description: "Work on innovative automotive software solutions...",
        source: "Employee Referral",
      },
      {
        id: 12,
        title: "Big Data Engineer",
        company: "Twitter",
        location: "San Francisco, CA, USA",
        description: "Handle and process massive data pipelines...",
        source: "LinkedIn",
      },
      {
        id: 13,
        title: "Digital Marketing Manager",
        company: "Spotify",
        location: "New York, NY, USA",
        description: "Plan and execute digital marketing campaigns...",
        source: "Glassdoor",
      },
      {
        id: 14,
        title: "Product Manager",
        company: "Slack",
        location: "Vancouver, Canada",
        description: "Lead the development of innovative products...",
        source: "Company Careers",
      },
      {
        id: 15,
        title: "Backend Developer",
        company: "Airbnb",
        location: "Dublin, Ireland",
        description: "Build reliable and scalable backend systems...",
        source: "Online Job Board",
      },
      {
        id: 16,
        title: "Hardware Engineer",
        company: "Intel",
        location: "Santa Clara, CA, USA",
        description: "Design and test cutting-edge hardware systems...",
        source: "Indeed",
      },
      {
        id: 17,
        title: "Robotics Engineer",
        company: "Boston Dynamics",
        location: "Waltham, MA, USA",
        description: "Develop state-of-the-art robotics systems...",
        source: "Recruiter Outreach",
      },
      {
        id: 18,
        title: "Graphic Designer",
        company: "Adobe",
        location: "San Jose, CA, USA",
        description: "Create visually compelling designs...",
        source: "Job Portal",
      },
      {
        id: 19,
        title: "Artificial Intelligence Researcher",
        company: "OpenAI",
        location: "San Francisco, CA, USA",
        description: "Push the boundaries of AI research and innovation...",
        source: "Glassdoor Recommendations",
      },
      {
        id: 20,
        title: "Mobile App Developer",
        company: "Snapchat",
        location: "Los Angeles, CA, USA",
        description: "Develop intuitive and engaging mobile applications...",
        source: "Company Website",
      },
      {
        id: 21,
        title: "SEO Specialist",
        company: "HubSpot",
        location: "Cambridge, MA, USA",
        description: "Optimize website content for search engines...",
        source: "LinkedIn",
      },
    ];
    setAllJobs(mockAllJobs);
  }, [navigate]);

  // Filtered Jobs
  const filteredJobs = allJobs.filter(
    (job) =>
      job.title.toLowerCase().includes(searchTitle.toLowerCase()) &&
      job.company.toLowerCase().includes(searchCompany.toLowerCase()) &&
      job.location.toLowerCase().includes(searchLocation.toLowerCase())
  );

  // Render the Jobs Page only if logged in
  if (!isLoggedIn) return null;

  return (
    <div className="p-6 pt-24 max-w-5xl mx-auto">
      {/* Search Bar */}
      <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mb-6 bg-gray-100 p-4 rounded-lg shadow">
        <input
          type="text"
          placeholder="Find your perfect job"
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
          className="p-3 border border-gray-300 rounded-md w-full sm:w-2/3 focus:ring-2 focus:ring-[#00ADB5]"
        />
        <input
          type="text"
          placeholder="Company"
          value={searchCompany}
          onChange={(e) => setSearchCompany(e.target.value)} // Handle company search
          className="p-3 border border-gray-300 rounded-md w-full sm:w-1/3 focus:ring-2 focus:ring-[#00ADB5]"
        />
        <input
          type="text"
          placeholder="Location"
          value={searchLocation}
          onChange={(e) => setSearchLocation(e.target.value)}
          className="p-3 border border-gray-300 rounded-md w-full sm:w-1/3 focus:ring-2 focus:ring-[#00ADB5]"
        />
      </div>

      {/* Tabs */}
      <div className="flex justify-center gap-10 border-b mb-6">
        <button
          className={`pb-2 text-lg font-semibold ${
            activeTab === "forYou"
              ? "border-b-4 border-[#00ADB5] text-[#00ADB5]"
              : "text-gray-600"
          }`}
          onClick={() => setActiveTab("forYou")}
        >
          For you
        </button>
        <button
          className={`pb-2 text-lg font-semibold ${
            activeTab === "search"
              ? "border-b-4 border-[#00ADB5] text-[#00ADB5]"
              : "text-gray-600"
          }`}
          onClick={() => setActiveTab("search")}
        >
          Search
        </button>
      </div>

      {/* Tab Content */}
      {activeTab === "forYou" && (
        <div>
          <h2 className="text-2xl font-semibold mb-4 text-[#00ADB5]">
            Recommended Jobs
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {recommendedJobs.map((job) => (
              <JobCard key={job.id} job={job} />
            ))}
          </div>
        </div>
      )}

      {activeTab === "search" && (
        <div>
          <h2 className="text-2xl font-semibold mb-4 text-[#00ADB5]">
            All Jobs
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredJobs.map((job) => (
              <JobCard key={job.id} job={job} />
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default JobsPage;
