import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import JobCard from "../components/JobCard";
import axios from "axios";
import {jwtDecode} from "jwt-decode";

const JobsPage = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("forYou");
  const [recommendedJobs, setRecommendedJobs] = useState([]);
  const [allJobs, setAllJobs] = useState([]);
  const [searchTitle, setSearchTitle] = useState("");
  const [searchCompany, setSearchCompany] = useState("");
  const [searchLocation, setSearchLocation] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [filteredJobs, setFilteredJobs] = useState([]);

  const getUserEmail = () => {
    const token = localStorage.getItem("authToken");
    if (!token) return null;

    try {
      const decodedToken = jwtDecode(token);
      return decodedToken.sub;
    } catch (error) {
      console.error("Invalid token:", error);
      return null;
    }
  };

  useEffect(() => {
    // Check if user is logged in
    const user = localStorage.getItem("authToken");
    if (user) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
      navigate("/login");
    }

    const initializeJobs = async () => {
      setLoading(true);
      try {
        const email = getUserEmail();
        if (!email) throw new Error("Unable to fetch user email.");

        // Fetch recommended jobs
        const recommendedResponse = await axios.get(
          `http://localhost:8080/api/jobs/recommended/${email}`
        );

        const recommended = recommendedResponse.data;

        // Fetch all jobs
        const allResponse = await axios.get("http://localhost:8080/api/jobs/all");
        const all = allResponse.data;

        // Combine recommended jobs and similar jobs from all jobs
        const getCombinedRecommendedJobs = (recommended, all) => {
          const isTitleSimilar = (title1, title2) => {
            const normalize = (str) =>
              str ? str.toLowerCase().replace(/[^a-zA-Z0-9 ]/g, "") : "";
            return normalize(title1).includes(normalize(title2));
          };

          return recommended.map((recJob) => {
            const similarJobs = all.filter((job) =>
              isTitleSimilar(recJob.role, job.role)
            );
            return [recJob, ...similarJobs];
          }).flat();
        };

        const combinedRecommendedJobs = getCombinedRecommendedJobs(recommended, all);

        setRecommendedJobs(combinedRecommendedJobs);
        setAllJobs(all);
        setFilteredJobs(all); // Initially show all jobs in the search tab
      } catch (err) {
        console.error("Error fetching jobs:", err);
        setError("Error fetching job data. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    initializeJobs();
  }, [navigate]);

  useEffect(() => {
    // Automatically filter jobs based on search inputs
    const filtered = allJobs.filter(
      (job) =>
        (job.role && job.role.toLowerCase().includes(searchTitle.toLowerCase())) &&
        (job.company && job.company.toLowerCase().includes(searchCompany.toLowerCase())) &&
        (job.location && job.location.toLowerCase().includes(searchLocation.toLowerCase()))
    );
    setFilteredJobs(filtered);
    setActiveTab("search");
  }, [searchTitle, searchCompany, searchLocation, allJobs]);

  if (!isLoggedIn) return null;

  return (
    <div className="p-6 pt-24 max-w-5xl mx-auto">
      {/* Search Bar */}
      <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mb-6 bg-gray-100 p-4 rounded-lg shadow">
        <input
          type="text"
          placeholder="Find your perfect job"
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
          className="p-3 border border-gray-300 rounded-md w-full sm:w-2/3 focus:ring-2 focus:ring-[#00ADB5]"
        />
        <input
          type="text"
          placeholder="Company"
          value={searchCompany}
          onChange={(e) => setSearchCompany(e.target.value)}
          className="p-3 border border-gray-300 rounded-md w-full sm:w-1/3 focus:ring-2 focus:ring-[#00ADB5]"
        />
        <input
          type="text"
          placeholder="Location"
          value={searchLocation}
          onChange={(e) => setSearchLocation(e.target.value)}
          className="p-3 border border-gray-300 rounded-md w-full sm:w-1/3 focus:ring-2 focus:ring-[#00ADB5]"
        />
      </div>

      {/* Tabs */}
      <div className="flex justify-center gap-10 border-b mb-6">
        <button
          className={`pb-2 text-lg font-semibold ${
            activeTab === "forYou"
              ? "border-b-4 border-[#00ADB5] text-[#00ADB5]"
              : "text-gray-600"
          }`}
          onClick={() => setActiveTab("forYou")}
        >
          For you
        </button>
        <button
          className={`pb-2 text-lg font-semibold ${
            activeTab === "search"
              ? "border-b-4 border-[#00ADB5] text-[#00ADB5]"
              : "text-gray-600"
          }`}
          onClick={() => setActiveTab("search")}
        >
          Search
        </button>
      </div>

      {/* Tab Content */}
      {activeTab === "forYou" && (
        <div>
          <h2 className="text-2xl font-semibold mb-4 text-[#00ADB5]">
            Recommended Jobs
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {recommendedJobs.map((job) => (
              <JobCard key={job.id} job={job} />
            ))}
          </div>
        </div>
      )}

      {activeTab === "search" && (
        <div>
          <h2 className="text-2xl font-semibold mb-4 text-[#00ADB5]">All Jobs</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {loading ? (
              <p>Loading jobs...</p>
            ) : filteredJobs.length > 0 ? (
              filteredJobs.map((job) => <JobCard key={job.id} job={job} />)
            ) : (
              <p className="text-gray-500">No jobs found.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default JobsPage;
