// src/components/JobCard.js
import React from "react";

const JobCard = ({ job }) => {
  return (
    <div className="bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-all">
      <h2 className="text-2xl font-semibold text-gray-800">{job.title}</h2>
      <p className="text-lg text-blue-600 mt-2">{job.company}</p>
      <p className="text-sm text-gray-600">{job.location}</p>
      <p className="text-sm text-gray-500 mt-4">{job.shortDescription}</p>

      <div className="flex justify-between items-center mt-4">
        <span className="text-sm text-gray-600">{job.jobType}</span>
        <button className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition">
          Apply Now
        </button>
      </div>

      {/* Add the Source of the Job */}
      <p className="text-xs text-gray-400 mt-4">
        <strong>Source: </strong>
        <a href={job.sourceUrl} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">
          {job.source}
        </a>
      </p>
    </div>
  );
};

export default JobCard;
