import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Signup from "./pages/Signup";
import Login from "./pages/Login";
import Profile from "./pages/Profile";
import JobsPage from "./pages/JobsPage";
import LandingPage from "./pages/LandingPage";
import AdminPage from "./pages/AdminPage"; // Import the AdminPage

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null); // New state to track user role

  useEffect(() => {
    // Check if user is already authenticated on page load
    const loggedInUser = localStorage.getItem("loggedInUser");
    const role = localStorage.getItem("userRole"); // Fetch user role from localStorage
    setIsAuthenticated(!!loggedInUser);
    setUserRole(role); // Set user role if it exists
  }, []);

  return (
    <Router>
      <Navbar
        isAuthenticated={isAuthenticated}
        setIsAuthenticated={setIsAuthenticated}
        userRole={userRole} // Pass userRole to Navbar
      />
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/jobs" element={<JobsPage />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />

        {/* Protect Profile Route: Only show if authenticated */}
        <Route
          path="/profile"
          element={isAuthenticated ? <Profile /> : <Login setIsAuthenticated={setIsAuthenticated} />}
        />

        {/* Admin Route: Only accessible for admin users */}
        <Route
          path="/admin"
          element={
            isAuthenticated && userRole === "admin" ? (
              <AdminPage />
            ) : (
              <Login setIsAuthenticated={setIsAuthenticated} />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
