import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const skillsOptions = [
  "JavaScript",
  "Python",
  "Java",
  "C++",
  "React",
  "Node.js",
  "SQL",
  "CSS",
  "HTML",
  "AWS",
];

const Signup = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
    gender: "",
    location: "",
    skills: [],
    role: "user", // Set role to "user" by default
  });

  const [dropdownOpen, setDropdownOpen] = useState(false); // Dropdown state
  const [searchTerm, setSearchTerm] = useState(""); // Search term state
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSkillSelect = (skill) => {
    if (!formData.skills.includes(skill)) {
      setFormData({ ...formData, skills: [...formData.skills, skill] });
    }
    setSearchTerm(""); // Clear search term after selection
  };

  const handleSkillRemove = (skill) => {
    setFormData({
      ...formData,
      skills: formData.skills.filter((s) => s !== skill),
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if passwords match
    if (formData.password !== formData.confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    // Store user data in LocalStorage
    const existingUsers = JSON.parse(localStorage.getItem("users")) || [];
    const isUserExists = existingUsers.some((user) => user.email === formData.email);

    if (isUserExists) {
      alert("Email is already registered.");
    } else {
      existingUsers.push(formData);
      localStorage.setItem("users", JSON.stringify(existingUsers));
      alert("Signup successful!");
      navigate("/login");
    }
  };

  // Filter skills based on the search term
  const filteredSkills = skillsOptions.filter((skill) =>
    skill.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-[#222831] flex items-center justify-center pt-16 mt-[20px]">
      <div className="container mx-auto p-6 bg-[#393E46] text-white shadow-xl rounded-lg max-w-xl">
        <h1 className="text-3xl font-extrabold text-center mb-8 text-[#00ADB5]">
          Create Your Account
        </h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Full Name */}
          <div>
            <label className="block text-sm font-semibold">
              Full Name <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
              required
            />
          </div>

          {/* Other fields here... */}

          {/* Skills */}
          <div>
            <label className="block text-sm font-semibold">
              Skills <span className="text-red-500">*</span>
            </label>
            <div className="relative">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
                placeholder="Search and select skills"
                onClick={() => setDropdownOpen(true)}
              />
              {dropdownOpen && (
                <ul className="absolute z-10 w-full bg-[#393E46] border border-[#00ADB5] rounded-md mt-1 max-h-40 overflow-auto">
                  {filteredSkills.map((skill) => (
                    <li
                      key={skill}
                      className="p-2 hover:bg-[#00ADB5] cursor-pointer"
                      onClick={() => {
                        handleSkillSelect(skill);
                        setDropdownOpen(false); // Close dropdown after selection
                      }}
                    >
                      {skill}
                    </li>
                  ))}
                  {filteredSkills.length === 0 && (
                    <li className="p-2 text-center text-gray-400">No skills found</li>
                  )}
                </ul>
              )}
            </div>

            {/* Selected Skills */}
            <div className="flex items-center flex-wrap mt-2 gap-2">
              {formData.skills.map((skill) => (
                <div
                  key={skill}
                  className="bg-[#00ADB5] text-[#222831] px-3 py-1 rounded-full flex items-center"
                >
                  {skill}
                  <button
                    type="button"
                    onClick={() => handleSkillRemove(skill)}
                    className="ml-2 text-red-600"
                  >
                    &times;
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Submit Button */}
          <div className="text-center">
            <button
              type="submit"
              className="bg-[#00ADB5] text-white py-3 px-6 rounded-md hover:bg-[#00A8A0] transition duration-300"
            >
              Sign Up
            </button>
          </div>
        </form>

        {/* Login Redirect */}
        <div className="text-center mt-4">
          Already have an account?{" "}
          <button
            onClick={() => navigate("/login")}
            className="text-[#00ADB5] hover:underline"
          >
            Login here
          </button>
        </div>
      </div>
    </div>
  );
};

export default Signup;
