// src/components/HeroSection.jsx
import React, { useState } from 'react';

const HeroSection = () => {
  const [query, setQuery] = useState('');

  const handleSearch = () => {
    alert(`Searching for: ${query}`);
  };

  return (
    <div className="flex flex-col items-center justify-center text-center bg-blue-50 py-20 px-4">
      <h2 className="text-4xl font-bold mb-4">Find Your Dream Job</h2>
      <p className="text-lg text-gray-600 mb-6">Search from thousands of job listings tailored just for you.</p>
      <div className="flex w-full max-w-md space-x-2">
        <input
          type="text"
          className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-blue-500"
          placeholder="Job title, keywords, or company"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700"
        >
          Search
        </button>
      </div>
    </div>
  );
};

export default HeroSection;
