import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ isAuthenticated, setIsAuthenticated }) => {
  const navigate = useNavigate();
  const userRole = localStorage.getItem("userRole"); // Get the user role from localStorage

  useEffect(() => {
    // Check if the user is logged in
    const loggedInUser = localStorage.getItem("loggedInUser");
    setIsAuthenticated(!!loggedInUser); // Set isAuthenticated based on the presence of a logged-in user
  }, [setIsAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser"); // Remove user data from localStorage
    localStorage.removeItem("userRole");    // Remove the role from localStorage
    setIsAuthenticated(false);             // Update state to reflect logged-out status
    navigate("/login");                    // Redirect to the login page
  };

  const handleJobsClick = () => {
    if (isAuthenticated) {
      navigate("/JobsPage"); // Navigate to the jobs page if authenticated
    } else {
      alert("Please Login First"); // Alert and redirect to login page if not authenticated
      navigate("/login");
    }
  };

  return (
    <nav className="bg-gray-800 text-white p-4 fixed w-full top-0 left-0 z-50 shadow-lg">
      <div className="flex justify-between items-center">
        {/* Logo Section */}
        <div className="text-2xl font-bold">
          <Link to="/" className="hover:bg-gray-700 p-2 rounded">
            Job-Currator
          </Link>
        </div>

        {/* Links Section */}
        <div className="space-x-6 flex items-center ml-4">
          {/* Jobs Button */}
          <button
            onClick={handleJobsClick}
            className="hover:bg-gray-700 p-2 rounded"
          >
            Jobs
          </button>

          {/* Show Sign Up and Login only if not authenticated */}
          {!isAuthenticated && (
            <>
              <Link to="/signup" className="hover:bg-gray-700 p-2 rounded">
                Sign Up
              </Link>
              <Link to="/login" className="hover:bg-gray-700 p-2 rounded">
                Login
              </Link>
            </>
          )}

          {/* Show Profile and Logout if authenticated */}
          {isAuthenticated && (
            <>
              <Link to="/profile" className="hover:bg-gray-700 p-2 rounded">
                Profile
              </Link>

              {/* Show Admin Panel if the user is an admin */}
              {userRole === "admin" && (
                <Link to="/admin" className="hover:bg-gray-700 p-2 rounded">
                  Admin Panel
                </Link>
              )}

              <button
                onClick={handleLogout}
                className="bg-red-500 text-white p-2 rounded hover:bg-red-700"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
