// src/components/JobCard.jsx
import React from "react";

const JobCard = ({ job }) => {
  return (
    <div className="bg-[#222831] text-white rounded-lg shadow-lg p-6 flex flex-col justify-between h-full">
      <div className="flex flex-col space-y-4">
        {/* Job Title */}
        <h2 className="text-2xl font-semibold">{job.title}</h2>

        {/* Company Name */}
        <p className="text-lg font-medium text-[#00ADB5]">{job.company}</p>

        {/* Job Location */}
        <p className="text-gray-400">{job.location}</p>

        {/* Description */}
        <p className="text-gray-300">{job.description}</p>
      </div>

      <div className="mt-6">
        {/* Source */}
        <div className="flex justify-between items-center">
          <span className="text-sm text-gray-400">Source: {job.source}</span>
          <button className="bg-[#00ADB5] text-white px-4 py-2 rounded-lg hover:bg-[#00A8A0] transition duration-300">
            Apply Now
          </button>
        </div>
      </div>
    </div>
  );
};

export default JobCard;
