import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = ({ setIsAuthenticated }) => {
  const [loginData, setLoginData] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const existingUsers = JSON.parse(localStorage.getItem("users")) || [];
    const user = existingUsers.find((user) => user.email === loginData.email);

    if (user) {
      if (user.password === loginData.password) {
        alert("Login successful!");
        
        // Ensure role is set to "user" by default if not present
        const loggedInUser = { ...user, role: user.role || "user" };
        
        // Store the authenticated user and role in localStorage
        localStorage.setItem("loggedInUser", JSON.stringify(loggedInUser));

        // Set the isAuthenticated state to true
        setIsAuthenticated(true);

        // Navigate based on the user's role (admin or regular user)
        if (loggedInUser.role === "admin") {
          navigate("/admin-dashboard"); // Admin dashboard
        } else {
          navigate("/jobs"); // Regular user dashboard
        }
      } else {
        alert("Incorrect password!");
      }
    } else {
      alert("No account found with this email.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#222831]">
      <div className="container p-6 bg-[#393E46] text-white shadow-xl rounded-lg max-w-md">
        <h1 className="text-3xl font-extrabold text-center mb-8 text-[#00ADB5]">
          Login
        </h1>
        <form onSubmit={handleLogin} className="space-y-6">
          <div>
            <label className="block text-sm font-semibold">Email Address</label>
            <input
              type="email"
              name="email"
              value={loginData.email}
              onChange={handleChange}
              className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-semibold">Password</label>
            <input
              type="password"
              name="password"
              value={loginData.password}
              onChange={handleChange}
              className="w-full p-3 bg-[#222831] text-white border border-[#00ADB5] rounded-md focus:outline-none focus:ring-2 focus:ring-[#00ADB5]"
              required
            />
          </div>
          <div className="text-center">
            <button
              type="submit"
              className="bg-[#00ADB5] text-white py-3 px-6 rounded-md hover:bg-[#00A8A0] transition duration-300"
            >
              Login
            </button>
          </div>
        </form>
        <div className="text-center mt-4">
          Don't have an account?{" "}
          <button
            onClick={() => navigate("/signup")}
            className="text-[#00ADB5] hover:underline"
          >
            Sign Up here
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
