// export default Profile;


import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import JobCard from "../components/JobCard";
import {jwtDecode} from "jwt-decode"; // Fixed import

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [bookmarkedJobs, setBookmarkedJobs] = useState([]);
  const [showPasswordFields, setShowPasswordFields] = useState(false);
  const navigate = useNavigate();

  const getUserEmail = () => {
    const token = localStorage.getItem("authToken");
    if (!token) return null;

    try {
      const decodedToken = jwtDecode(token);
      return decodedToken.sub; // Ensure 'sub' contains the email in your JWT
    } catch (error) {
      console.error("Invalid token:", error);
      return null;
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      const email = getUserEmail();
      if (!email) {
        navigate("/login");
        return;
      }

      try {
        const { data: user } = await axios.get(`http://localhost:8080/api/users/${email}`);
        const { data: bookmarks } = await axios.get(`http://localhost:8080/api/bookmarks/${email}`);
        console.log(bookmarks);
        const { data: jobs } = await axios.get(`http://localhost:8080/api/jobs/all`);

        setUserData(user);

        // Correctly map bookmarked job details
        const bookmarkedDetails = bookmarks
          .map((bookmark) => jobs.find((job) => job.id === bookmark.job_id))
          .filter((job) => job); // Remove nulls if any bookmark doesn't match a job

        setBookmarkedJobs(bookmarkedDetails);
      } catch (error) {
        console.error("Error fetching user data:", error);
        navigate("/login");
      }
    };

    fetchData();
  }, [navigate]);

  const handleSave = async (e) => {
    e.preventDefault();

    try {
      const email = getUserEmail();
      if (showPasswordFields) {
        const { currentPassword, newPassword, confirmPassword } = passwordData;
        if (!currentPassword || !newPassword || !confirmPassword) {
          alert("Please fill in all password fields.");
          return;
        }

        if (newPassword !== confirmPassword) {
          alert("New password and confirm password do not match.");
          return;
        }

        await axios.post(`http://localhost:8080/api/users/update/${email}`, {
          ...userData,
          currentPassword,
          newPassword,
        });
        alert("Password updated successfully.");
      } else {
        await axios.post(`http://localhost:8080/api/users/update/${email}`, userData);
      }

      setIsEditing(false);
      setShowPasswordFields(false);
      setPasswordData({ currentPassword: "", newPassword: "", confirmPassword: "" });
    } catch (error) {
      console.error("Error updating user data:", error);
      alert("Failed to update profile.");
    }
  };

  const handleInputChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({ ...passwordData, [e.target.name]: e.target.value });
  };

  if (!userData) {
    return (
      <div className="min-h-screen bg-gray-100 pt-16 flex justify-center items-center">
        <div>Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 pt-16 px-4">
      <div className="container mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="col-span-1 bg-white shadow-xl rounded-lg p-8 relative lg:sticky lg:top-16 h-max">
          <h1 className="text-3xl font-semibold text-center mb-6 text-gray-800">Your Profile</h1>
          <form onSubmit={handleSave} className="space-y-6">
            {Object.keys(userData).map((field) =>
              !["bookmarkedJobs", "skills", "password"].includes(field) ? (
                <div className="flex justify-between" key={field}>
                  <strong className="text-lg text-gray-600 capitalize">{field}:</strong>
                  {isEditing ? (
                    <input
                      type="text"
                      name={field}
                      value={userData[field]}
                      onChange={handleInputChange}
                      className="border p-2 rounded-md w-40"
                    />
                  ) : (
                    <span className="text-lg text-gray-900">{userData[field]}</span>
                  )}
                </div>
              ) : null
            )}

            <div className="flex justify-between">
              <strong className="text-lg text-gray-600">Skills:</strong>
              {isEditing ? (
                <input
                  type="text"
                  name="skills"
                  value={userData.skills?.join(", ") || ""}
                  onChange={(e) =>
                    setUserData({ ...userData, skills: e.target.value.split(",").map((s) => s.trim()) })
                  }
                  className="border p-2 rounded-md w-40"
                />
              ) : (
                <span className="text-lg text-gray-900">{userData.skills?.join(", ") || "No skills added"}</span>
              )}
            </div>

            {showPasswordFields && (
              <>
                <div className="flex justify-between">
                  <strong className="text-lg text-gray-600">Current Password:</strong>
                  <input
                    type="password"
                    name="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    className="border p-2 rounded-md w-40"
                  />
                </div>
                <div className="flex justify-between">
                  <strong className="text-lg text-gray-600">New Password:</strong>
                  <input
                    type="password"
                    name="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                    className="border p-2 rounded-md w-40"
                  />
                </div>
                <div className="flex justify-between">
                  <strong className="text-lg text-gray-600">Confirm Password:</strong>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={passwordData.confirmPassword}
                    onChange={handlePasswordChange}
                    className="border p-2 rounded-md w-40"
                  />
                </div>
              </>
            )}

            <div className="flex space-x-4 mt-6">
              {isEditing ? (
                <>
                  <button
                    type="submit"
                    className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
                  >
                    Save
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowPasswordFields(!showPasswordFields)}
                    className="bg-yellow-500 text-white px-4 py-2 rounded-md hover:bg-yellow-600"
                  >
                    {showPasswordFields ? "Cancel Password Change" : "Change Password"}
                  </button>
                </>
              ) : (
                <button
                  type="button"
                  onClick={() => setIsEditing(true)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
                >
                  Edit Profile
                </button>
              )}
            </div>
          </form>
        </div>

        <div className="col-span-2 bg-white shadow-xl rounded-lg p-8">
          <h2 className="text-3xl font-semibold text-center mb-6 text-gray-800">Saved Jobs</h2>
          {bookmarkedJobs.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {bookmarkedJobs.map((job) => (
                <JobCard job={job} key={job.id} showDetails={true} />
              ))}
            </div>
          ) : (
            <p className="text-center text-gray-500">You haven't bookmarked any jobs yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
