// src/pages/JobsPage.js
import React, { useState, useEffect } from "react";
import JobCard from "./components/JobCard";

const JobsPage = () => {
  const [jobs, setJobs] = useState([
    {
      id: 1,
      title: "Software Engineer",
      company: "TechCorp",
      location: "New York, NY",
      jobType: "Full-time",
      shortDescription: "Looking for a passionate Software Engineer to join our dynamic team.",
      source: "Indeed", // New source property
      sourceUrl: "https://www.indeed.com", // URL of the source website
    },
    {
      id: 2,
      title: "Product Manager",
      company: "InnovateX",
      location: "San Francisco, CA",
      jobType: "Full-time",
      shortDescription: "We need a Product Manager to lead the development of our next big project.",
      source: "LinkedIn", // New source property
      sourceUrl: "https://www.linkedin.com", // URL of the source website
    },
    {
      id: 3,
      title: "UI/UX Designer",
      company: "Designify",
      location: "Los Angeles, CA",
      jobType: "Part-time",
      shortDescription: "Join our creative team and design stunning user interfaces for our products.",
      source: "Glassdoor", // New source property
      sourceUrl: "https://www.glassdoor.com", // URL of the source website
    },
  ]);

  useEffect(() => {
    // Fetch jobs from an API if required
    // setJobs(fetchedJobs);
  }, []);

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Job Listings</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {jobs.map((job) => (
          <JobCard key={job.id} job={job} />
        ))}
      </div>
    </div>
  );
};

export default JobsPage;
