import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import JobCard from "../components/JobCard";

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [bookmarkedJobs, setBookmarkedJobs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
    const bookmarks = JSON.parse(localStorage.getItem("bookmarkedJobs")) || [];

    if (loggedInUser) {
      setUserData(loggedInUser);
      setBookmarkedJobs(bookmarks);
    } else {
      navigate("/login");
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    navigate("/login");
  };

  const handleSave = () => {
    localStorage.setItem("loggedInUser", JSON.stringify(userData));
    setIsEditing(false);
  };

  const handleInputChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  if (!userData) {
    return (
      <div className="min-h-screen bg-gray-100 pt-16 flex justify-center items-center">
        <div>Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 pt-16 px-4">
      <div className="container mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Side: Profile Section */}
        <div className="col-span-1 bg-white shadow-xl rounded-lg p-8">
          <h1 className="text-3xl font-semibold text-center mb-6 text-gray-800">Your Profile</h1>
          <div className="space-y-6">
            {Object.keys(userData).map((field) =>
              field !== "bookmarkedJobs" && field !== "skills" ? (
                <div className="flex justify-between" key={field}>
                  <strong className="text-lg text-gray-600 capitalize">{field}:</strong>
                  {isEditing ? (
                    <input
                      type="text"
                      name={field}
                      value={userData[field]}
                      onChange={handleInputChange}
                      className="border p-2 rounded-md w-40"
                    />
                  ) : (
                    <span className="text-lg text-gray-900">{userData[field]}</span>
                  )}
                </div>
              ) : null
            )}

            {/* Skills Section */}
            <div className="flex justify-between">
              <strong className="text-lg text-gray-600">Skills:</strong>
              {isEditing ? (
                <input
                  type="text"
                  name="skills"
                  value={userData.skills.join(", ")}
                  onChange={(e) =>
                    setUserData({ ...userData, skills: e.target.value.split(",").map((s) => s.trim()) })
                  }
                  className="border p-2 rounded-md w-40"
                />
              ) : (
                <span className="text-lg text-gray-900">{userData.skills.join(", ")}</span>
              )}
            </div>

            {/* Action Buttons */}
            <div className="flex space-x-4 mt-6">
              {isEditing ? (
                <button
                  onClick={handleSave}
                  className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
                >
                  Save
                </button>
              ) : (
                <button
                  onClick={() => setIsEditing(true)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
                >
                  Edit Profile
                </button>
              )}
              <button
                onClick={handleLogout}
                className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600"
              >
                Logout
              </button>
            </div>
          </div>
        </div>

        {/* Right Side: Bookmarked Jobs Section */}
        <div className="col-span-2">
          <h2 className="text-3xl font-semibold text-center mb-6 text-gray-800">Saved Jobs</h2>
          {bookmarkedJobs.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {bookmarkedJobs.map((job) => (
                <JobCard job={job} key={job.id} />
              ))}
            </div>
          ) : (
            <p className="text-center text-gray-500">You haven't bookmarked any jobs yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
