// src/pages/Login.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = ({ setIsAuthenticated }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Mock validation, replace with real authentication logic
    const mockUserData = {
      fullName: "John Doe",
      email: email,
      phone: "1234567890",
      location: "New York",
      gender: "Male",
      experienceLevel: "Mid Level",
      skills: "JavaScript, React",
      profilePicture: "/profile-pic.jpg", // Sample image path
    };

    // Save user data to localStorage (for mock purposes)
    localStorage.setItem("userData", JSON.stringify(mockUserData));
    setIsAuthenticated(true);
    navigate("/profile");
  };

  return (
    <div className="min-h-screen bg-gray-100 mt-20"> {/* Add margin-top */}
      <div className="container mx-auto p-6 bg-white shadow-lg rounded-lg">
        <h1 className="text-2xl font-bold text-center mb-6">Login</h1>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-semibold">Email Address</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-semibold">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              required
            />
          </div>
          <div className="text-center">
            <button
              type="submit"
              className="bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700"
            >
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
